function copyToClipAndroid(val){var Context=plus.android.importClass("android.content.Context");var main=plus.android.runtimeMainActivity();var clip=main.getSystemService(Context.CLIPBOARD_SERVICE);plus.android.invoke(clip,"setText",val);mui.toast("复制成功")}function copyToClipIos(val){var UIPasteboard=plus.ios.importClass("UIPasteboard");var generalPasteboard=UIPasteboard.generalPasteboard();generalPasteboard.plusCallMethod({setValue:val,forPasteboardType:"public.utf8-plain-text"});generalPasteboard.plusCallMethod({valueForPasteboardType:"public.utf8-plain-text"});mui.toast("复制成功")}